CREATE TABLE IF NOT EXISTS `contract` (  `id` SERIAL NOT NULL AUTO_INCREMENT ,  `project` varchar(50) NOT NULL,  `number` varchar(20) NOT NULL,  `type` varchar(1) NOT NULL DEFAULT '0' COMMENT '0 = persona física; 1 = representante asociacion; 2 = apoderado entidad mercantil',  `name` tinytext DEFAULT NULL,  `surname` tinytext DEFAULT NULL,  `nif` varchar(10) DEFAULT NULL,  `office` tinytext DEFAULT NULL COMMENT 'Cargo en la asociación o empresa',  `address` tinytext DEFAULT NULL,  `location` varchar(255) DEFAULT NULL,  `region` varchar(255) DEFAULT NULL,  `country` varchar(50) DEFAULT NULL,    `entity_name` tinytext DEFAULT NULL,  `entity_cif` varchar(10) DEFAULT NULL,  `entity_address` tinytext DEFAULT NULL,  `entity_location` varchar(255) DEFAULT NULL,  `entity_region` varchar(255) DEFAULT NULL,  `entity_country` varchar(50) DEFAULT NULL,  `reg_name` tinytext DEFAULT NULL COMMENT 'Registro de asociaciones o nombre del notario',  `reg_number` tinytext DEFAULT NULL COMMENT 'Número de registro o número de protocolo del notario',  `reg_id` tinytext DEFAULT NULL COMMENT 'Número en el registro mercantil',  `project_name` tinytext DEFAULT NULL COMMENT 'Nombre del proyecto',  `project_url` varchar(255) DEFAULT NULL COMMENT 'URL del proyecto',  `project_owner` tinytext DEFAULT NULL COMMENT 'Nombre del impulsor',  `project_user` tinytext DEFAULT NULL COMMENT 'Nombre del usuario autor del proyecto',  `project_profile` varchar(255) DEFAULT NULL COMMENT 'URL del perfil del autor del proyecto',    `project_description` TEXT DEFAULT NULL COMMENT 'Breve descripción del proyecto',  PRIMARY KEY (`id`),  KEY `project` (`project`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Contratos';-- campos de cuentasALTER TABLE `contract` ADD `bank` TINYTEXT DEFAULT NULL;ALTER TABLE `contract` ADD `bank_owner` TINYTEXT DEFAULT NULL;ALTER TABLE `contract` ADD `paypal` TINYTEXT DEFAULT NULL;ALTER TABLE `contract` ADD `paypal_owner` TINYTEXT DEFAULT NULL;